const WorkExperience = [
    {
        id: 1,
        title: "AI Researcher",
        company: "CVC Lab, Stony Brook University",
        yearsActive: "January 2025 - Present",
        information: [
            "Conducted research under Prof. Arie Kaufman at the Center for Visual Computing (CVC Lab), developing neural compression techniques for 4D medical imaging.",
            "Optimized multi-resolution hash encoding and deep learning-based quantization for efficient retrieval and real-time radiology visualization.",
            "Automated model tuning and benchmarking across large-scale medical datasets to enhance AI-driven diagnostic workflows.",
        ],
    },
    {
        id: 2,
        title: "Research Assistant - AI & Cloud Computing",
        company: "SUNY-IBM Research Project",
        yearsActive: "January 2025 - Present",
        information: [
            "Collaborated with Prof. Erez Zadok to develop AI-driven task scheduling using Deep Q-Learning and Proximal Policy Optimization (PPO).",
            "Reduced compute latency for large-scale AI workloads by 40% through reinforcement learning-based adaptive resource allocation.",
            "Integrated Transformer-based workload distribution models to optimize parallel AI training on cloud platforms.",
        ],
    },
    {
        id: 3,
        title: "Machine Learning Intern",
        company: "Qualcomm AI Research",
        yearsActive: "July 2023 - December 2023",
        information: [
            "Achieved a 5x latency reduction in edge AI models by implementing quantization techniques using AIMET and TensorRT.",
            "Optimized deep learning models for Snapdragon-based embedded AI platforms, improving real-time inference efficiency.",
            "Conducted extensive testing on model compression methods to enhance performance for resource-constrained environments.",
        ],
    },
    {
        id: 4,
        title: "Computer Vision Research Intern",
        company: "AI Lab, RMK Engineering College",
        yearsActive: "July 2021 - May 2022",
        information: [
            "Developed an AI-driven hand gesture recognition system using radar-based 3D-CNNs and Spiking Neural Networks.",
            "Achieved 97% accuracy for gesture-based interfaces, enabling real-time human-computer interaction for accessibility solutions.",
            "Integrated signal processing techniques and NLP-based speech synthesis to develop an ASL-to-voice system.",
        ],
    },
    {
        id: 5,
        title: "Software Development Intern",
        company: "TCS Innovation Labs",
        yearsActive: "June 2020 - September 2020",
        information: [
            "Designed and deployed scalable REST APIs for AI-driven applications in cloud environments.",
            "Enhanced model inference pipelines by integrating optimized deep learning frameworks such as ONNX and TensorFlow Lite.",
            "Developed automation scripts for model evaluation and dataset preprocessing, streamlining AI workflows.",
        ],
    }
];

