const WorkExperience = [
    {
        id: 1,
        title: "Research Assistant – AI/ML",
        company: "Stony Brook University",
        yearsActive: "January 2025 - Present",
        information: [
            "Conducting research on AI-driven workload scheduling for large-scale LLM training, in collaboration with IBM Research.",
            "Developing Reinforcement Learning (RL)-based task scheduling algorithms using Deep Q-Learning (DQN) and Proximal Policy Optimization (PPO), optimizing GPU cluster utilization for 40% faster fine-tuning of Large Language Models (LLMs).",
            "Engineering a Transformer-based resource allocation framework, enabling adaptive scaling and reducing compute overhead by 25% in distributed AI training.",
            "Designing a self-optimizing ML workload orchestrator using Graph Neural Networks (GNNs) and Attention Mechanisms to predict optimal task execution sequences.",
            "Leveraging LLM-driven optimization techniques for cloud-based AI workloads, ensuring efficient scheduling of multi-modal ML pipelines across heterogeneous computing environments.",
        ],
    },
    {
        id: 2,
        title: "Software Engineer - Machine Learning",
        company: "MultiCoreWare Inc.",
        yearsActive: "December 2021 - July 2024",
        information: [
            "Progressed from Project Intern (Dec 2021 - June 2022) to Software Engineer (July 2022 - July 2024).",
            "Led quantization and optimization of GKT, BEVDet, BEVFusion, Cavaface, Rankpose, and Img2Pose models for autonomous vehicles, reducing latency by up to 10x, boosting inference speed by 40%, and increasing edge FPS by over 10x.",
            "Redesigned deep learning architectures for 2D and 3D object detection models (BTCDet, PointPillars, YOLO, RetinaNet), reducing parameter count by 30% while maintaining 98% of original accuracy.",
            "Implemented advanced model quantization using AIMET (AI Model Efficiency Toolkit), utilizing cross-layer equalization, AdaRound, and mixed precision to optimize deep learning models.",
            "Optimized GKT model, achieving a 15% improvement in system performance and 15x latency reduction on the Qualcomm Snapdragon 8cx Gen 3, earning the Customer Delight Award.",
        ],
    },
    {
        id: 3,
        title: "Machine Learning Intern",
        company: "Qualcomm AI Research",
        yearsActive: "July 2023 - December 2023",
        information: [
            "Achieved a 5x latency reduction in edge AI models by implementing quantization techniques using AIMET and TensorRT.",
            "Optimized deep learning models for Snapdragon-based embedded AI platforms, improving real-time inference efficiency.",
            "Conducted extensive testing on model compression methods to enhance performance for resource-constrained environments.",
        ],
    },
    {
        id: 4,
        title: "Project Intern",
        company: "MultiCoreWare Inc.",
        yearsActive: "December 2021 - June 2022",
        information: [
            "Worked on deep learning model optimization for computer vision applications, focusing on quantization and acceleration techniques.",
            "Enhanced efficiency of neural network models for autonomous vehicle applications, improving real-time processing capabilities.",
        ],
